import React, { useState } from 'react';

const App = () =>{
    const bg="red";
     const [bgc,setBg]=useState(bg)
    const bgChange = ()=>{
    let newBg="yellow";
    setBg(newBg);
        
    };

    return( 
    <>
     <div style = {{backgroundColor:bgc}}>
     <button onClick = {bgChange}>click me</button>
       
     </div>

    </>);
};
export default App;
