import React, { useState } from 'react';

const App = () =>{
    const bg="red";
     const [bgc,setBg]=useState(bg);
     const [name,setName]=useState("click me");
    const bgChange = ()=>{
    let newBg="yellow";
    setBg(newBg);
     setName("ouch ")  
    };

    return( 
    <>
     <div style = {{backgroundColor:bgc}}>
     <button onClick = {bgChange}>{name}</button>
       
     </div>

    </>);
};
export default App;
